import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ProjectDetails.css'; 

function ProjectDetails() {
  const { id } = useParams();
  const navigate = useNavigate();

  const projects = [
    {
      id: 1,
      title: 'Inventaris Sekolah',
      image: 'project1.png',
      details: 'Aplikasi inventaris sekolah adalah aplikasi yang digunakan untuk mengelola data barang-barang yang ada di sekolah. Aplikasi ini memiliki fitur CRUD (Create, Read, Update, Delete) yang memudahkan pengguna dalam mengelola data barang. Aplikasi ini dibuat menggunakan ReactJS dan Firebase.',
    },
    {
      id: 2,
      title: 'Project 2',
      image: 'project1.png',
      details: 'More details about Project 2...',
    },
    {
      id: 3,
      title: 'Project 3',
      image: 'project1.png',
      details: 'More details about Project 3...',
    },
  ];

  const project = projects.find((p) => p.id === parseInt(id));

  if (!project) {
    return <div>Project not found</div>;
  }

  return (
    <section id="project-details">
      <h2>{project.title}</h2>
      <div className="project-content">
        <div className="image-container">
          <img 
            src={require(`./image/${project.image}`)} 
            alt={project.title} 
            className="full-resolution-image"
          />
        </div>
        <div className="project-info">
          <p>{project.details}</p>
          <button onClick={() => navigate(-1)}>Back to Projects</button>
        </div>
      </div>
    </section>
  );
}

export default ProjectDetails;
