import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useParams, useNavigate } from 'react-router-dom';
import AboutMe from './AboutMe';
import Projects from './Projects';
import Certificate from './Certificate';
import SocialMedia from './SocialMedia';
import { FaBars, FaTimes } from 'react-icons/fa'; 
import logo from './image/logo.jpg'; 

function Nav() {
  const [isNavOpen, setIsNavOpen] = useState(false); // State to manage navbar visibility

  const toggleNav = () => {
    setIsNavOpen(!isNavOpen); // Toggle navbar visibility
  };

  return (
    <Router>
      <div className="Nav">
        <header>
          <div className="logo">
            <img src={logo} alt="Logo" />
          </div>
          <nav>
            <div className="hamburger" onClick={toggleNav}>
              {isNavOpen ? <FaTimes /> : <FaBars />} {/* Toggle between hamburger and close icon */}
            </div>
            <ul className={isNavOpen ? 'nav-links open' : 'nav-links'}>
              <li><Link to="/about" onClick={toggleNav}>About Me</Link></li>
              <li><Link to="/projects" onClick={toggleNav}>Projects</Link></li>
              <li><Link to="/certificate" onClick={toggleNav}>Certificate</Link></li>
              <li><Link to="/social-media" onClick={toggleNav}>Social Media</Link></li>
            </ul>
          </nav>
        </header>
        <Routes>
          <Route path="/" element={
            <>
              <AboutMe />
              <Projects />
              <Certificate />
              <SocialMedia />
            </>
          } />
          <Route path="/project/:id" element={<ProjectDetails />} />
        </Routes>
      </div>
    </Router>
  );
}

function ProjectDetails() {
  const { id } = useParams();
  const navigate = useNavigate();

  // Example project data (replace with real data or fetch from an API)
  const projects = [
    { id: 1, title: 'Project 1', description: 'Detailed description of Project 1.' },
    { id: 2, title: 'Project 2', description: 'Detailed description of Project 2.' },
    { id: 3, title: 'Project 3', description: 'Detailed description of Project 3.' },
    { id: 4, title: 'Project 4', description: 'Detailed description of Project 4.' },
  ];

  const project = projects.find((p) => p.id === parseInt(id));

  if (!project) {
    return <div>Project not found</div>;
  }

  return (
    <section id="project-details">
      <h2>{project.title}</h2>
      <p>{project.description}</p>
      <button onClick={() => navigate(-1)}>Back to Projects</button>
    </section>
  );
}

export default Nav;
