import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ProjectDetails.css'; 

function ProjectDetails() {
  const { id } = useParams();
  const navigate = useNavigate();

  const projects = [
    {
      id: 1,
      title: 'Inventaris Sekolah',
      image: 'project1.png',
      details: 'Aplikasi inventaris sekolah adalah aplikasi yang digunakan untuk mengelola data barang-barang yang ada di sekolah.Dan juga murid dan guru dapat meminjam barang melalui web ini. Aplikasi ini memiliki fitur CRUD (Create, Read, Update, Delete) yang memudahkan pengguna dalam mengelola data barang. Aplikasi ini dibuat menggunakan php dengan template AdminLte.',
      image2: 'project1-2.png',
      details2: 'pada page pertama terdapat form login yang digunakan untuk login ke dalam aplikasi. Setelah login berhasil maka akan masuk ke dalam dashboard aplikasi. Pada dashboard terdapat menu-menu yang dapat digunakan untuk mengelola data barang, data peminjaman, data pengembalian, data murid, data guru, dan data user. Pada menu data barang terdapat fitur CRUD yang dapat digunakan untuk mengelola data barang. Pada menu data peminjaman terdapat fitur CRUD yang dapat digunakan untuk mengelola data peminjaman. Pada menu data pengembalian terdapat fitur CRUD yang dapat digunakan untuk mengelola data pengembalian. Pada menu data murid terdapat fitur CRUD yang dapat digunakan untuk mengelola data murid. Pada menu data guru terdapat fitur CRUD yang dapat digunakan untuk mengelola data guru. Pada menu data user terdapat fitur CRUD yang dapat digunakan untuk mengelola data user.',
    },
    {
      id: 2,
      title: 'Project 2',
      image: 'project1.png',
      details: 'More details about Project 2...',
    },
    {
      id: 3,
      title: 'Project 3',
      image: 'project1.png',
      details: 'More details about Project 3...',
    },
  ];

  const project = projects.find((p) => p.id === parseInt(id));

  if (!project) {
    return <div>Project not found</div>;
  }

  return (
    <section id="project-details">
      <h2>{project.title}</h2>
      <div className="project-content">
        <div className="image-container">
          <img 
            src={require(`./image/${project.image}`)} 
            alt={project.title} 
            className="full-resolution-image"
          />
        </div>
        <div className="project-info">
          <p>{project.details}</p>          
        </div>
        <div className="image-container">
          <img 
            src={require(`./image/${project.image2}`)} 
            alt={project.title} 
            className="full-resolution-image"
          />
        </div>
        <div className="project-info">
          <p>{project.details2}</p>          
        </div>
        <div>
          <button onClick={() => navigate(-1)}>Back to Projects</button>
          </div>
      </div>
    </section>
  );
}

export default ProjectDetails;
